mapper.select:
SELECT MEMBER_ID, USERNAME, PASSWORD, EMAIL, NICKNAME, ROLE, ZIPCODE,
       STREETADDRESS, DETAILADDRESS, CREATED_AT, MODIFIED_AT
FROM MEMBER_T;

SELECT USERNAME, NICKNAME, PASSWORD, ROLE, MEMBER_ID
  FROM MEMBER_T
 WHERE USERNAME = #{username}

SELECT USERNAME, PASSWORD, ROLE
  FROM MEMBER_T
 WHERE USERNAME = #{username}

SELECT MEMBER_ID
  FROM MEMBER_T
 WHERE USERNAME = #{username}

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
  FROM MEMBER_T
 WHERE EMAIL = #{email}

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
  FROM MEMBER_T
 WHERE USERNAME = #{username}

SELECT CASE WHEN COUNT(*) > 0 THEN true ELSE false END
  FROM MEMBER_T
 WHERE NICKNAME = #{nickname}

mapper.update:
UPDATE MEMBER_T
   SET PASSWORD = #{password}
 WHERE USERNAME = #{username}

UPDATE MEMBER_T
SET NICKNAME = #{nickname}
WHERE USERNAME = #{username}

mapper.select:
SELECT USERNAME
FROM MEMBER_T
WHERE email = #{email};
