mapper.delete:
DELETE
  FROM Product p
 WHERE p.prod_id = #{prodId}

mapper.select:
SELECT p.prod_id, i.pi_stored_url, p.prod_name, p.prod_description, p.prod_price, p.prod_stock_quantity, u.user_nickname, p.cate_id, c.category_name FROM Product p LEFT JOIN ProductImage i ON p.prod_id = i.prod_id LEFT JOIN User u ON p.seller_id = u.user_id LEFT JOIN CATEGORY_T C ON p.cate_id = C.CATEGORY_ID WHERE 1=1
AND p.prod_name LIKE CONCAT('%', #{searchDto.prodName},'%')
AND u.user_nickname LIKE CONCAT('%', #{searchDto.userNickname}, '%')
AND p.prod_price >= #{searchDto.startPrice}
AND p.prod_price <= #{searchDto.endPrice}
AND P.cate_id = #{searchDto.cateId}
ORDER BY p.prod_id DESC LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM Product p LEFT JOIN ProductImage i ON p.prod_id = i.prod_id LEFT JOIN User u ON p.seller_id = u.user_id WHERE 1=1
AND p.prod_name LIKE CONCAT('%', #{prodName}, '%')
AND u.user_nickname LIKE CONCAT('%', #{userNickname}, '%')
AND P.cate_id = #{cateId}
AND PROD_PRICE BETWEEN #{startPrice} AND #{endPrice}

mapper.update:
UPDATE Product p
SET p.prod_stock_quantity = #{adjustment}
WHERE p.prod_id = #{prodId}

mapper.select:
SELECT p.prod_stock_quantity
FROM Product p
LEFT JOIN Cart C ON p.prod_id = c.prod_id
WHERE p.prod_id = #{prodId}
GROUP BY p.prod_id;

SELECT p.prod_stock_quantity
FROM Product p
WHERE p.prod_id = #{prodId}

mapper.update:
UPDATE Product p
SET p.prod_stock_quantity = p.prod_stock_quantity + #{quantity}
WHERE p.prod_id = #{prodId}

UPDATE Product p
SET p.prod_stock_quantity = p.prod_stock_quantity - #{quantity}
WHERE p.prod_id = #{prodId}
