FROM SHIPPING_ADDRESS_T
WHERE MEMBER_ID = #{memberId};

SELECT ADDRESS_ID, MEMBER_ID, RECIPIENT_NAME, MOBILE, ZIPCODE, STREETADDRESS, DETAILADDRESS, DEFAULT_ADDRESS
FROM SHIPPING_ADDRESS_T
