  FROM SHIPPING_ADDRESS_T
 WHERE user_id = #{userId}

SELECT ADDRESS_ID, user_id, RECIPIENT_NAME, MOBILE, ZIPCODE, STREETADDRESS, DETAILADDRESS, DEFAULT_ADDRESS
FROM SHIPPING_ADDRESS_T
WHERE user_id = #{userId};

SELECT ADDRESS_ID, MEMBER_ID, RECIPIENT_NAME, MOBILE, ZIPCODE, STREETADDRESS, DETAILADDRESS, DEFAULT_ADDRESS
FROM SHIPPING_ADDRESS_T
WHERE ADDRESS_ID = #{addressId};

SELECT ADDRESS_ID, user_id, RECIPIENT_NAME, MOBILE, ZIPCODE, STREETADDRESS, DETAILADDRESS, DEFAULT_ADDRESS
FROM SHIPPING_ADDRESS_T
WHERE user_id = #{userId} AND DEFAULT_ADDRESS = 'Y';
