mapper.select:
SELECT COUNT(*)
  FROM WISHLIST_T
 WHERE MEMBER_ID = #{memberId} AND PRODUCT_ID = #{productId}

mapper.delete:
DELETE FROM WISHLIST_T
 WHERE MEMBER_ID = #{memberId}
   AND PRODUCT_ID = #{productId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN TRUE ELSE FALSE END
  FROM WISHLIST_T
 WHERE MEMBER_ID = #{memberId} AND PRODUCT_ID = #{productId}
